const Profile = require('../models/Profile')

module.exports = {

	get: (params) => {
		return new Promise((resolve, reject) => {
			Profile.find(params)
			.then(profiles => {
				resolve(Profile.convertToJson(profiles))
			})
			.catch(err => {
				reject(err)
			})
		})
	},

	getById: (id) => {
		return new Promise((resolve, reject) => {
			Profile.findById(id)
			.then(profile => {
				resolve(profile)
			})
			.catch(err => {
				reject(new Error(Profile.resourceName + ' ' + id + ' not found.'))
			})
		})
	},

	post: (body) => {
		return new Promise((resolve, reject) => {
			Profile.create(body)
			.then(profile => {
				resolve(profile)
			})
			.catch(err => {
				reject(err)
			})
		})
	},

	put: (id, params) => {
		return new Promise((resolve, reject) => {
			Profile.findByIdAndUpdate(id, params, {new:true})
			.then(profile => {
				resolve(profile)
			})
			.catch(err => {
				reject(err)
			})
		})
	},

	delete: (id) => {
		return new Promise((resolve, reject) => {
			Profile.findByIdAndRemove(id)
			.then(() => {
				resolve()
			})
			.catch(err => {
				reject(err)
			})
		})
	}

}
